mapper.select:
select user_pw
from User
where user_id = #{user_id}

select *
from User
where user_id = #{user_id}

select *
from User
where user_id = #{id}

select *
from QnA
where qa_num = #{qa_num}

mapper.update:
update QnA
set qa_title   = #{qa_title},
    qa_content = #{qa_content}
where qa_num = #{qa_num}

mapper.select:
select * from JobPostingList j
left join Company c on j.com_num = c.com_num
where 1 = 1

    and j.jpl_locationSi in

        #{cri.jpl_locationSi}




    and j.jpl_locationGu in

        #{cri.jpl_locationGu}




    and j.jpl_duty in

        #{cri.jpl_duty}




    and j.jpl_workHistory in

        #{cri.jpl_workHistory}




    and j.jpl_education in

        #{cri.jpl_education}




    and j.jpl_salary in

        #{cri.jpl_salary}




    and j.jpl_certificate in

        #{cri.jpl_certificate}




    and j.jpl_conditions in

        #{cri.jpl_conditions}




    and j.jpl_workForm in

        #{cri.jpl_workForm}




    and j.jpl_title like
    concat('%', #{cri.inputSearch}, '%') or
    com_name like
    concat('%', #{cri.inputSearch}, '%')





        order by j.jpl_num desc


        and j.jpl_endDate >= DATE_FORMAT(now(), '%Y-%m-%d')
        order by j.jpl_endDate asc


        order by j.jpl_salary desc


limit #{cri.pageStart}, #{cri.amount}

select  count(*) from JobPostingList j
left join Company c on j.com_num = c.com_num
where 1 = 1

    and j.jpl_locationSi in

        #{cri.jpl_locationSi}




    and j.jpl_locationGu in

        #{cri.jpl_locationGu}




    and j.jpl_duty in

        #{cri.jpl_duty}




    and j.jpl_workHistory in

        #{cri.jpl_workHistory}




    and j.jpl_education in

        #{cri.jpl_education}




    and j.jpl_salary in

        #{cri.jpl_salary}




    and j.jpl_certificate in

        #{cri.jpl_certificate}




    and j.jpl_conditions in

        #{cri.jpl_conditions}




    and j.jpl_workForm in

        #{cri.jpl_workForm}




    and j.jpl_title like
    concat('%', #{cri.inputSearch}, '%') or
    com_name like
    concat('%', #{cri.inputSearch}, '%')





        order by j.jpl_num desc


        and j.jpl_endDate >= DATE_FORMAT(now(), '%Y-%m-%d')
        order by j.jpl_endDate asc


        order by j.jpl_salary desc

select *
from ResumeList
where user_id = #{user_id}

select *
from ResumeList
where res_num = #{res_num}

select *
from Education
where res_num = #{res_num}

select *
from WorkExperiences
where res_num = #{res_num}

select *
from Certificate
where res_num = #{res_num}

mapper.insert:
insert into Certificate(cert_num, res_num)
values(#{cert_num}, #{res_num})
on duplicate key update cert_name = #{cert_name}, cert_gainDate = #{cert_gainDate}, cert_issueInstitute = #{cert_issueInstitute}
